  // Improved cart fetching with error handling
  const fetchCartItems = async (userId) => {
    try {
      const cartRef = doc(db, "carts", userId);
      const cartSnap = await getDoc(cartRef);

      if (!cartSnap.exists()) {
        // Initialize cart if it doesn't exist
        await setDoc(cartRef, {
          items: [],
          createdAt: new Date(),
          updatedAt: new Date(),
          total: 0
        });
        setCartItems([]);
        return [];
      }

      const cartData = cartSnap.data();
      const items = cartData.items || [];

      // Filter out any invalid items
      const validItems = items.filter(item => 
        item && item.productId && typeof item.quantity === 'number'
      );

      if (validItems.length === 0) {
        setCartItems([]);
        return [];
      }

      const productIds = validItems.map(item => item.productId);

      // Fetch product data in batches of 10 (Firestore limit for 'in' queries)
      const mergedItems = [];

      for (let i = 0; i < productIds.length; i += 10) {
        const batchIds = productIds.slice(i, i + 10);

        if (batchIds.length > 0) {
          const productsQuery = query(
            collection(db, "products"),
            where(documentId(), "in", batchIds)
          );

          const productsSnap = await getDocs(productsQuery);
          const productsMap = {};

          productsSnap.forEach(doc => {
            productsMap[doc.id] = {
              id: doc.id,
              ...doc.data()
            };
          });

          // Process this batch of items
          validItems
            .filter(item => batchIds.includes(item.productId))
            .forEach(item => {
              mergedItems.push({
                ...item,
                product: productsMap[item.productId] || {
                  id: item.productId,
                  name: "Product not found",
                  price: 0,
                  imageUrl: "/placeholder.jpg"
                }
              });
            });
        }
      }

      setCartItems(mergedItems);
      return mergedItems;

    } catch (error) {
      console.error("Error fetching cart items:", error);
      Toast.show({
        type: 'error',
        text1: 'Failed to load cart items'
      });
      setCartItems([]);
      return [];
    }
  };

