import { Ionicons } from "@expo/vector-icons";
import { LinearGradient } from "expo-linear-gradient";
import { router } from "expo-router";
import { createUserWithEmailAndPassword, signInWithEmailAndPassword, onAuthStateChanged } from "firebase/auth";
import { doc, setDoc, getDoc } from "firebase/firestore";
import React, { useState, useEffect } from "react";
import {
  ActivityIndicator,
  Alert,
  KeyboardAvoidingView,
  Platform,
  SafeAreaView,
  ScrollView,
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  View
} from "react-native";
import AsyncStorage from '@react-native-async-storage/async-storage';
import { auth, db } from "../firebaseConfig"; // Adjust the path as necessary

export default function LoginScreen() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [fullName, setFullName] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [isLogin, setIsLogin] = useState(true);
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [initialLoading, setInitialLoading] = useState(true);

  // Check if user is already logged in on component mount
  useEffect(() => {
    const checkAuthState = async () => {
      try {
        // Check AsyncStorage for stored user data
        const storedUserData = await AsyncStorage.getItem('userData');

        if (storedUserData) {
          const userData = JSON.parse(storedUserData);
          // Verify the token is still valid by checking current auth state
          const unsubscribe = onAuthStateChanged(auth, async (user) => {
            if (user && user.uid === userData.uid) {
              // User is still authenticated, redirect to home
              router.replace("/home");
            } else {
              // Token expired or invalid, clear storage
              await AsyncStorage.removeItem('userData');
              setInitialLoading(false);
            }
            unsubscribe();
          });
        } else {
          // No stored data, check current auth state
          const unsubscribe = onAuthStateChanged(auth, (user) => {
            if (user) {
              // User is authenticated but no stored data, store and redirect
              storeUserData(user);
              router.replace("/home");
            } else {
              setInitialLoading(false);
            }
            unsubscribe();
          });
        }
      } catch (error) {
        console.error('Error checking auth state:', error);
        setInitialLoading(false);
      }
    };

    checkAuthState();
  }, []);

  // Function to store user data in AsyncStorage
  const storeUserData = async (user) => {
    try {
      // Get additional user data from Firestore
      const userDocRef = doc(db, "users", user.uid);
      const userDoc = await getDoc(userDocRef);

      const userData = {
        uid: user.uid,
        email: user.email,
        displayName: user.displayName,
        photoURL: user.photoURL,
        emailVerified: user.emailVerified,
        lastLoginAt: new Date().toISOString(),
        ...(userDoc.exists() ? userDoc.data() : {})
      };

      await AsyncStorage.setItem('userData', JSON.stringify(userData));
      console.log('User data stored successfully');
    } catch (error) {
      console.error('Error storing user data:', error);
    }
  };

  // Function to clear user data from AsyncStorage
  const clearUserData = async () => {
    try {
      await AsyncStorage.removeItem('userData');
      console.log('User data cleared successfully');
    } catch (error) {
      console.error('Error clearing user data:', error);
    }
  };

  const handleSignup = async () => {
    if (!email || !password || !fullName || !phoneNumber) {
      Alert.alert("Error", "Please fill in all fields");
      return;
    }

    if (password.length < 6) {
      Alert.alert("Error", "Password must be at least 6 characters long");
      return;
    }

    setLoading(true);
    try {
      // Create user account
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      // Save additional user data to Firestore
      const userDocData = {
        uid: user.uid,
        email: email.toLowerCase(),
        fullName: fullName.trim(),
        phoneNumber: phoneNumber.trim(),
        createdAt: new Date().toISOString(),
        profileCompleted: false, // Will be true after location selection
        isActive: true,
        locationSelected: false // Track if user has selected location
      };

      await setDoc(doc(db, "users", user.uid), userDocData);

      // Store user data in AsyncStorage
      await storeUserData(user);

      // Clear form
      setEmail("");
      setPassword("");
      setFullName("");
      setPhoneNumber("");

      // Navigate to home page using Expo Router
      router.replace("/home");

    } catch (err) {
      let errorMessage = "An error occurred during signup";

      switch (err.code) {
        case 'auth/email-already-in-use':
          errorMessage = "This email is already registered. Please use a different email or try logging in.";
          break;
        case 'auth/invalid-email':
          errorMessage = "Please enter a valid email address.";
          break;
        case 'auth/weak-password':
          errorMessage = "Password is too weak. Please choose a stronger password.";
          break;
        default:
          errorMessage = err.message;
      }

      Alert.alert("Signup Error", errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const handleLogin = async () => {
    if (!email || !password) {
      Alert.alert("Error", "Please fill in all fields");
      return;
    }

    setLoading(true);
    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      // Store user data in AsyncStorage
      await storeUserData(user);

      // Clear form on successful login
      setEmail("");
      setPassword("");

      // Navigate to main app after successful login
      router.replace("/home");

    } catch (err) {
      let errorMessage = "An error occurred during login";

      switch (err.code) {
        case 'auth/user-not-found':
          errorMessage = "No account found with this email. Please check your email or sign up.";
          break;
        case 'auth/wrong-password':
          errorMessage = "Incorrect password. Please try again.";
          break;
        case 'auth/invalid-email':
          errorMessage = "Please enter a valid email address.";
          break;
        case 'auth/too-many-requests':
          errorMessage = "Too many failed attempts. Please try again later.";
          break;
        case 'auth/invalid-credential':
          errorMessage = "Invalid email or password. Please check your credentials.";
          break;
        default:
          errorMessage = err.message;
      }

      Alert.alert("Login Error", errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const handleForgotPassword = () => {
    // Navigate to forgot password screen
    router.push("/forgot-password");
  };

  const handleSocialLogin = (provider) => {
    // Handle social login based on provider
    console.log(`Social login with ${provider}`);
    // You can implement actual social login logic here
  };

  // Show loading screen while checking authentication state
  if (initialLoading) {
    return (
      <SafeAreaView style={styles.container}>
        <LinearGradient
          colors={['#667eea', '#764ba2']}
          style={[styles.gradient, styles.loadingContainer]}
        >
          <View style={styles.logoContainer}>
            <Ionicons name="storefront" size={50} color="#fff" />
          </View>
          <Text style={styles.brandName}>Lakesmart</Text>
          <ActivityIndicator 
            size="large" 
            color="#fff" 
            style={{ marginTop: 20 }} 
          />
          <Text style={[styles.tagline, { marginTop: 16 }]}>
            Loading...
          </Text>
        </LinearGradient>
      </SafeAreaView>
    );
  }

  return (
    <SafeAreaView style={styles.container}>
      <LinearGradient
        colors={['#667eea', '#764ba2']}
        style={styles.gradient}
      >
        <KeyboardAvoidingView
          behavior={Platform.OS === "ios" ? "padding" : "height"}
          style={styles.keyboardView}
        >
          <ScrollView 
            contentContainerStyle={styles.scrollContainer}
            showsVerticalScrollIndicator={false}
          >
            {/* Header */}
            <View style={styles.header}>
              <View style={styles.logoContainer}>
                <Ionicons name="storefront" size={50} color="#fff" />
              </View>
              <Text style={styles.brandName}>Lakesmart</Text>
              <Text style={styles.tagline}>‚ÄúWhy just shop, when you can LakeSmart?‚Äù</Text>
            </View>

            {/* Form Container */}
            <View style={styles.formContainer}>
              {/* Toggle Buttons */}
              <View style={styles.toggleContainer}>
                <TouchableOpacity
                  style={[styles.toggleButton, isLogin && styles.activeToggle]}
                  onPress={() => setIsLogin(true)}
                >
                  <Text style={[styles.toggleText, isLogin && styles.activeToggleText]}>
                    Login
                  </Text>
                </TouchableOpacity>
                <TouchableOpacity
                  style={[styles.toggleButton, !isLogin && styles.activeToggle]}
                  onPress={() => setIsLogin(false)}
                >
                  <Text style={[styles.toggleText, !isLogin && styles.activeToggleText]}>
                    Sign Up
                  </Text>
                </TouchableOpacity>
              </View>

              {/* Form Fields */}
              <View style={styles.inputContainer}>
                {/* Name Field - Only for Signup */}
                {!isLogin && (
                  <View style={styles.inputWrapper}>
                    <Ionicons name="person-outline" size={20} color="#666" style={styles.inputIcon} />
                    <TextInput
                      style={styles.input}
                      placeholder="Full Name"
                      placeholderTextColor="#999"
                      value={fullName}
                      onChangeText={setFullName}
                      autoCapitalize="words"
                      autoCorrect={false}
                    />
                  </View>
                )}

                {/* Phone Field - Only for Signup */}
                {!isLogin && (
                  <View style={styles.inputWrapper}>
                    <Ionicons name="call-outline" size={20} color="#666" style={styles.inputIcon} />
                    <TextInput
                      style={styles.input}
                      placeholder="Phone Number"
                      placeholderTextColor="#999"
                      value={phoneNumber}
                      onChangeText={setPhoneNumber}
                      keyboardType="phone-pad"
                      autoCapitalize="none"
                      autoCorrect={false}
                    />
                  </View>
                )}

                <View style={styles.inputWrapper}>
                  <Ionicons name="mail-outline" size={20} color="#666" style={styles.inputIcon} />
                  <TextInput
                    style={styles.input}
                    placeholder="Email Address"
                    placeholderTextColor="#999"
                    value={email}
                    onChangeText={setEmail}
                    keyboardType="email-address"
                    autoCapitalize="none"
                    autoCorrect={false}
                  />
                </View>

                <View style={styles.inputWrapper}>
                  <Ionicons name="lock-closed-outline" size={20} color="#666" style={styles.inputIcon} />
                  <TextInput
                    style={[styles.input, styles.passwordInput]}
                    placeholder="Password"
                    placeholderTextColor="#999"
                    secureTextEntry={!showPassword}
                    value={password}
                    onChangeText={setPassword}
                    autoCapitalize="none"
                  />
                  <TouchableOpacity
                    style={styles.eyeIcon}
                    onPress={() => setShowPassword(!showPassword)}
                  >
                    <Ionicons
                      name={showPassword ? "eye-outline" : "eye-off-outline"}
                      size={20}
                      color="#666"
                    />
                  </TouchableOpacity>
                </View>

                {/* Password strength indicator for signup */}
                {!isLogin && password.length > 0 && (
                  <View style={styles.passwordStrength}>
                    <Text style={[
                      styles.passwordStrengthText,
                      password.length >= 6 ? styles.strongPassword : styles.weakPassword
                    ]}>
                      {password.length >= 6 ? "‚úì Strong password" : "‚ö† Password must be at least 6 characters"}
                    </Text>
                  </View>
                )}
              </View>

              {/* Forgot Password */}
              {isLogin && (
                <TouchableOpacity style={styles.forgotPassword} onPress={handleForgotPassword}>
                  <Text style={styles.forgotPasswordText}>Forgot Password?</Text>
                </TouchableOpacity>
              )}

              {/* Action Button */}
              <TouchableOpacity
                style={[styles.actionButton, loading && styles.disabledButton]}
                onPress={isLogin ? handleLogin : handleSignup}
                disabled={loading}
              >
                {loading ? (
                  <ActivityIndicator color="#fff" size="small" />
                ) : (
                  <Text style={styles.actionButtonText}>
                    {isLogin ? "Login" : "Create Account"}
                  </Text>
                )}
              </TouchableOpacity>

              {/* Social Login */}
              <View style={styles.socialContainer}>
                <View style={styles.dividerContainer}>
                  <View style={styles.divider} />
                  <Text style={styles.dividerText}>or continue with</Text>
                  <View style={styles.divider} />
                </View>

                <View style={styles.socialButtons}>
                  <TouchableOpacity 
                    style={styles.socialButton}
                    onPress={() => handleSocialLogin('google')}
                  >
                    <Ionicons name="logo-google" size={24} color="#db4437" />
                  </TouchableOpacity>
                  <TouchableOpacity 
                    style={styles.socialButton}
                    onPress={() => handleSocialLogin('apple')}
                  >
                    <Ionicons name="logo-apple" size={24} color="#000" />
                  </TouchableOpacity>
                  <TouchableOpacity 
                    style={styles.socialButton}
                    onPress={() => handleSocialLogin('facebook')}
                  >
                    <Ionicons name="logo-facebook" size={24} color="#4267b2" />
                  </TouchableOpacity>
                </View>
              </View>

              {/* Terms */}
              <Text style={styles.termsText}>
                By {isLogin ? "logging in" : "signing up"}, you agree to our{" "}
                <Text 
                  style={styles.linkText}
                  onPress={() => router.push("/terms-of-service")}
                >
                  Terms of Service
                </Text> and{" "}
                <Text 
                  style={styles.linkText}
                  onPress={() => router.push("https://example.com/privacy-policy")}
                >
                  Privacy Policy
                </Text>
              </Text>
            </View>
          </ScrollView>
        </KeyboardAvoidingView>
      </LinearGradient>
    </SafeAreaView>
  );
}

